package Week1Day2;

public class FindOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int number =10;
for(int i=1;i<=number;i++)
	
if (i%2==1)
	System.out.println(i+ " this is odd number");
else
	System.out.println(i+ " this is even number");
	}

	
}
